function remove(db, id, success) {
  db.collection("questions").doc(id).delete().then(() => {
    return success();
  }).catch(error => {
    console.error("Error removing document: ", error);
  });
}

function create(db, data, success) {
  db.collection("questions").add({
    question: data.question,
    type: data.type,
    options: data.options.map(item => {
      return {
        name: item.name,
        isCorrect: item.isCorrect === true
      };
    })
  }).then(docRef => {
    return success();
  }).catch(error => {
    console.error(error);
    toast.fire({
      icon: "error",
      title: "Creation question"
    });
  });
}

function update(db, data, success) {
  db.collection("questions").doc(data.id).set({
    question: data.question,
    type: data.type,
